    /**
     * renders the products variants tab content container
     * @return \Zend\View\Model\ViewModel
     */
    public function renderProductsPageContentTabVariantContentContainerAction()
    {
        $melisKey = $this->params()->fromRoute('melisKey', '');
        $productId = (int) $this->params()->fromQuery('productId', '');
    
        $melisTool = $this->getServiceLocator()->get('MelisCoreTool');
        $melisTool->setMelisToolKey('meliscommerce', 'meliscommerce_products');
        
        $columns = $melisTool->getColumns();
        $columns['actions'] = array('text' => 'Action', 'width' => '10%');
        $view = new ViewModel();
        $view->melisKey = $melisKey;
        $view->productId = $productId;
        $view->tableColumns = $columns;
        $view->getToolDataTableConfig = $melisTool->getDataTableConfiguration('#'.$productId.'_tableProductVariantList', true, false,array());
    
        return $view;
    
    }
    
    /**
     * generates the data needed for variant list table
     * 
     * @return \Zend\View\Model\JsonModel
     */
    public function renderProductsVariantDataAction()
    {
        $getValues = get_object_vars($this->getRequest()->getQuery());//echo '<pre>'; print_r($this->getRequest()->getPost()); echo '</pre>'; die();
        $productId = $this->getRequest()->getPost('prodId');
        $variantService = $this->getServiceLocator()->get('MelisComVariantService');
        $melisTool = $this->getServiceLocator()->get('MelisCoreTool');
        $melisTool->setMelisToolKey('meliscommerce', 'meliscommerce_products');
        $viewHelperManager = $this->getServiceLocator()->get('ViewHelperManager');
        $toolTipTable = $viewHelperManager->get('ToolTipTable');
        $docSvc = $this->getServiceLocator()->get('MelisComDocumentService');
    
        $colId = array();
        $dataCount = 0;
        $draw = 1;
        $tableData = array();
        $ctr = 0;
    
        //table layout
        $attrLayout     = '<span class="btn btn-default cell-val-table" style="border-radius: 4px;color: #7D7B7B;">%s</span>';
        $varOnline      = '<span class="text-success"><i class="fa fa-fw fa-circle"></i></span>';
        $varOffline     = '<span class="text-danger"><i class="fa fa-fw fa-circle"></i></span>';
        $varMain        = '<span class="text-success"><i class="fa fa-fw fa-star"></i></span>';
        $prodImage      = '<img src="%s" width="60"/>';
        $toolTipTextTag = '<a id="row-%s" class="toolTipHoverEvent tooltipTable" data-variantId="%s" data-variantname="%s" data-hasqtip="1" aria-describedby="qtip-%s">%s</a>';
    
        $colId = array_keys($melisTool->getColumns()); 
        
        $sortOrder = $this->getRequest()->getPost('order');
        $sortOrder = $sortOrder[0]['dir'];
        
        $selCol = $this->getRequest()->getPost('order');
        $selCol = $colId[$selCol[0]['column']];
        
        $draw = (int) $this->getRequest()->getPost('draw');
        
        $start = (int) $this->getRequest()->getPost('start');
        $length =  (int) $this->getRequest()->getPost('length');
        
        $search = $this->getRequest()->getPost('search');
        $search = $search['value'];
        if(!empty($selCol)){
            $order = $selCol. ' '. $sortOrder;
        }else{
            $order = 'var_main_variant '. $sortOrder;
        }
        
        foreach($variantService->getVariantListByProductId($productId, $this->getTool()->getCurrentLocaleID(), null, null, null, $start, $length, $search, $order) as $variantObj){
            
            $variant = $variantObj->getVariant();
            $attributes = '';
            $mainVariant = '';
            $variantStatus = '';
            foreach($variantObj->getAttributeValues() as $attributeValue){
                $col = 'avt_v_'.$attributeValue->atype_column_value;
                $attributes .= sprintf($attrLayout, $attributeValue->atval_trans[0]->$col);
            }

            if($variant->var_status){
                $variantStatus = $varOnline;
            }else{
                $variantStatus = $varOffline;
            }
             
            if($variant->var_main_variant){
                $mainVariant = $varMain;
            }
             
            $toolTipTable->setTable('productTable'.($ctr+1), 'table-row-'.($ctr+1), 'border:1px solid;');
            $toolTipTable->setColumns($this->getToolTipColumns());
            $sku = sprintf($toolTipTextTag, ($ctr+1), $variant->var_id, $variant->var_sku, ($ctr+1), $variant->var_sku) . $toolTipTable->render();
             
             
            $imgData = $docSvc->getDocumentsByRelationAndTypes('variant', $variantObj->getId(), 'IMG', 'DEFAULT');
            $variantimg = '';
            if($imgData->getDocument()) {
                $variantimg = sprintf($prodImage,$imgData->getDocument()[0]['doc_path'].$imgData->getDocument()[0]['doc_name']);  // to be updated SAME WITH MelisComProductListControler Line 205
            }else{
                $src = 'data:image/jpeg;base64,';    
                $variantimg = sprintf($prodImage,$src);
            }

            $tableData[] = array(
                'var_id' => $variant->var_id,
                'var_main_variant' => $mainVariant,
                'var_image' => $variantimg,
                'var_status' => $variantStatus,
                'var_sku' => $sku,
                'var_attributes' => $attributes,
                'DT_RowId' => $variant->var_id,
            );
            $ctr++;      
        }
        
        
        return new JsonModel(array(
            'draw' => (int) $draw,
            'recordsTotal' => $ctr,
            'recordsFiltered' => $ctr,
            'data' => $tableData,
        ));
    }
    
    /**
     * generates the data needed for variant list table
     *
     * @return \Zend\View\Model\JsonModel
     */
    public function renderProductsVariantTooltipDataAction()
    {
        $getValues = get_object_vars($this->getRequest()->getQuery());//echo '<pre>'; print_r($this->getRequest()->getPost()); echo '</pre>'; die();
        $productId = $getValues['prodId'];
        $variantService = $this->getServiceLocator()->get('MelisComVariantService');
        $melisTool = $this->getServiceLocator()->get('MelisCoreTool');
        $melisTool->setMelisToolKey('meliscommerce', 'meliscommerce_products');
        $viewHelperManager = $this->getServiceLocator()->get('ViewHelperManager');
        $toolTipTable = $viewHelperManager->get('ToolTipTable');
        $docSvc = $this->getServiceLocator()->get('MelisComDocumentService');
    
        $colId = array();
        $dataCount = 0;
        $draw = 1;
        $tableData = array();
        $ctr = 0;
    
        //table layout
        $attrLayout     = '<span class="btn btn-default cell-val-table" style="border-radius: 4px;color: #7D7B7B;">%s</span>';
        $varOnline      = '<span class="text-success"><i class="fa fa-fw fa-circle"></i></span>';
        $varOffline     = '<span class="text-danger"><i class="fa fa-fw fa-circle"></i></span>';
        $varMain        = '<span class="text-success"><i class="fa fa-fw fa-star"></i></span>';
        $prodImage      = '<img src="%s" width="60"/>';
        $toolTipTextTag = '<a id="row-%s" class="toolTipHoverEvent tooltipTable" data-variantId="%s" data-variantname="%s" data-hasqtip="1" aria-describedby="qtip-%s">%s</a>';

        foreach($variantService->getVariantListByProductId($productId, $this->getTool()->getCurrentLocaleID(), null, null, null, null, null, null, null) as $variantObj){
    
            $variant = $variantObj->getVariant();
            $attributes = '';
            $mainVariant = '';
            $variantStatus = '';
            foreach($variantObj->getAttributeValues() as $attributeValue){
                $col = 'avt_v_'.$attributeValue->atype_column_value;
                $attributes .= sprintf($attrLayout, $attributeValue->atval_trans[0]->$col);
            }
    
            if($variant->var_status){
                $variantStatus = $varOnline;
            }else{
                $variantStatus = $varOffline;
            }
             
            if($variant->var_main_variant){
                $mainVariant = $varMain;
            }
 
            $imgData = $docSvc->getDocumentsByRelationAndTypes('variant', $variantObj->getId(), 'IMG', 'DEFAULT');
            $variantimg = '';
            if($imgData->getDocument()) {
                $variantimg = sprintf($prodImage,$imgData->getDocument()[0]['doc_path'].$imgData->getDocument()[0]['doc_name']);  // to be updated SAME WITH MelisComProductListControler Line 205
            }else{
                $src = 'data:image/jpeg;base64,';
                $variantimg = sprintf($prodImage,$src);
            }
    
            $data = array(
                'var_id' => $variant->var_id,
                'var_main_variant' => $mainVariant,
                'var_image' => $variantimg,
                'var_status' => $variantStatus,
                'var_sku' => $variant->var_sku,
                'var_attributes' => $attributes,                
            );
            if($variant->var_main_variant){
                array_unshift($tableData, $data);
            }else{
                $tableData[] = $data;
            }
            $ctr++;
        }
    
//         echo '<pre>';var_dump($tableData); echo '</pre>'; die();
        return new JsonModel(array(
            'data' => $tableData,
        ));
    }
    
